                        $this->db->insert('sc_mst.option', $data);
                    }
                    header('Content-Type: application/json');
                    echo json_encode(
                        array(
                            'return' => true,
                            'info' => $info,
                            'body' => $body,
                        ), JSON_PRETTY_PRINT);
                    return true;
                } catch (\Exception $e) {
