    public function shuffle($len = 4)
    {
        $chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        $charcode = '';
        for ($loop = 0; $loop < $len; $loop++) {
            $charcode .= str_shuffle($chars)[0];
        }
        return $charcode;
    }

