class M_report extends CI_Model{
	
	function q_karout($periode){
		return	$this->db->query("select a.nik,a.nmlengkap,a.tglkeluarkerja,b.nmdept,c.keterangan from sc_mst.karyawan a
								left outer join sc_mst.departmen b on a.bag_dept=b.kddept
								left outer join sc_trx.status_kepegawaian c on a.nik=c.nik
								where to_char(tglkeluarkerja,'YYYYMM')='$periode' and statuskepegawaian='KO'
								union all
								select null as nik,'JUMLAH' as nmlengkap,null as tglkeluarkerja,null as nmdept, to_char(count(*),'999') as keterangan
								from sc_mst.karyawan a
								left outer join sc_mst.departmen b on a.bag_dept=b.kddept
								where to_char(tglkeluarkerja,'YYYYMM')='$periode' and statuskepegawaian='KO'");
	
	}
	
	function list_karout($periode){
		return $this->db->query("select a.nik,a.nmlengkap,a.tglkeluarkerja,b.nmdept,c.keterangan from sc_mst.karyawan a
								left outer join sc_mst.departmen b on a.bag_dept=b.kddept
								left outer join sc_trx.status_kepegawaian c on a.nik=c.nik
								where to_char(tglkeluarkerja,'YYYYMM')='$periode' and statuskepegawaian='KO'");
	
	}
	
	function q_karin($periode){
		return $this->db->query("select a.nik,a.nmlengkap,a.tglmasukkerja,b.nmdept from sc_mst.karyawan a
								left outer join sc_mst.departmen b on a.bag_dept=b.kddept
								where to_char(tglmasukkerja,'YYYYMM')='$periode' and statuskepegawaian<>'KO'
								union all
								select null as nik,'JUMLAH' as nmlengkap,null as tglmasukkerja, to_char(count(*),'999') as nmdept
								from sc_mst.karyawan a
								left outer join sc_mst.departmen b on a.bag_dept=b.kddept
								where to_char(tglmasukkerja,'YYYYMM')='$periode' and statuskepegawaian<>'KO'
								");
		
	}
	
	function list_karin($periode){
		return $this->db->query("select a.nik,a.nmlengkap,a.tglmasukkerja,b.nmdept from sc_mst.karyawan a
								left outer join sc_mst.departmen b on a.bag_dept=b.kddept
