	function q_absensimesin($nik,$tgl1,$tgl2){
		return $this->db->query("select a.badgenumber,b.nmlengkap,to_char(checktime,'YYYY-MM-DD') as tgl,to_char(checktime,'HH24:MI:SS') as jam,c.nmdept from sc_tmp.checkinout a,
								sc_mst.karyawan b,sc_mst.departmen c
								where a.badgenumber=b.nik and b.bag_dept=c.kddept and  to_char(a.checktime,'YYYY-MM-DD') between '$tgl1' and '$tgl2' 
								order by a.badgenumber,nmdept,checktime");
	
	}
	
